package json.rpc.java;

import com.github.arteam.simplejsonrpc.core.annotation.JsonRpcService;

@JsonRpcService
public class App {

    public App(int port) {
        App.listen(port);
    }

    // start the JSON-RPC server
    public static void main(String[] args) {
        listen(8080);
    }
    // creates HTTP server listener of your port of choice
    public static void listen(Integer port) {
        JavaHttpServer server = new JavaHttpServer(port, "/", HttpRequestHandler.getInstance());
        server.start();
        System.out.println("-- JSON-RPC Java server running on localhost port " + port);
    }

}
